package day04_increment_matematiksel_islemler;

public class C2_Preincrement_Postincrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int sayi1=20;
		
		int sayi2=++sayi1;
		
		
		
		System.out.println("pre-incrementsayi1 : "+ sayi1);// 21
		System.out.println("pre-incrementsayi2 : "+ sayi2);//21 
		
	    sayi2=sayi1++;
		
		int sayi3=15;
		int sayi4=sayi3++;
		
		System.out.println("post=increment : "+ sayi3);//16
		System.out.println("post-increment : "+ sayi4);//15
		
		
		
		
		
		
				
		

	}

}
